<li class="media-item" title="<?php echo e($item->name); ?>"><a href="<?php echo e($item instanceof \Juzaweb\Backend\Models\MediaFolder ? route('admin.media.folder', [$item->id]) : 'javascript:void(0)'); ?>" class="media-item-info <?php if($item instanceof \Juzaweb\Backend\Models\MediaFile): ?> media-file-item <?php endif; ?>" data-id="<?php echo e($item->id); ?>"> <?php
        $arr = $item->toArray();
