        $arr['is_file'] = $item instanceof \Juzaweb\Backend\Models\MediaFile ? 1 : 0;
        ?> <textarea class="d-none item-info"><?php echo json_encode($arr, 15, 512) ?></textarea><div class="attachment-preview"><div class="thumbnail"><div class="centered"> <?php if($item instanceof \Juzaweb\Backend\Models\MediaFolder): ?> <img src="<?php echo e(asset('jw-styles/juzaweb/images/folder.png')); ?>" alt="<?php echo e($item->name); ?>"><?php else: ?> <?php if($item->type == 'image'): ?> <img class="lazyload" src="data:image/gif;base64,R0lGODlhAQABAAAAACH5BAEKAAEALAAAAAABAAEAAAICTAEAOw==" data-src="<?php echo e(upload_url($item->path, null, '150x150')); ?>" alt="<?php echo e($item->name); ?>"> <?php else: ?> <img class="lazyload" src="data:image/gif;base64,R0lGODlhAQABAAAAACH5BAEKAAEALAAAAAABAAEAAAICTAEAOw==" data-src="<?php echo e(asset('jw-styles/juzaweb/images/file.png')); ?>" alt="<?php echo e($item->name); ?>"> <?php endif; ?> <?php endif; ?></div></div></div><div class="media-name"><span><?php echo e($item->name); ?></span></div></a></li><?php /**PATH E:\Laragon\cms\modules\Backend\Providers/../resources/views/backend/media/components/item.blade.php ENDPATH**/ ?>